export class NbMessage {
  constructor(msg) {
    this.files = [];
    this.quote = "";
    this.avatar = "";
    this.latitude = 0;
    this.longitude = 0;
    this.date = msg.createdAt;
    this.text = msg.content;
    if (msg.contentType == 'FILE') {
      this.type = "file";
      this.files = msg.files;
    } else {
      this.type = "text";
    }
  }
  updateUser(userName, imageUrl, reply) {
    this.reply = reply;
    this.avatar = imageUrl;
    this.senderName = userName;
  }
}
